item.script:
/********** automatic alias additions by LUX ************/

var members = []; //this is the array of names
var membersNS = []; //this is the array of names without spaces
$(".grid-item").each(function () {
  var member = $(this).find(".mem-alias").html().toLowerCase().trim();
  var memberArray = member.split(" ");
  var memberNS = ""; //set an empty variable for now
  if (member != "") {
    //if the member alias isn't empty, run the loop
    for (var i = 0; i < memberArray.length; i++) {
      //for each word in the member's alias, add the word to the empty variable so that it's a version without spaces
      memberNS = memberNS + memberArray[i];
    }
    if (jQuery.inArray(member, members) == -1) {
      //if it's not a duplicate, add the version WITH spaces to the members array for the label text
      members.push(member);
    }
    if (jQuery.inArray(memberNS, membersNS) == -1) {
      //same as above, but the no space version to the array to be used for data filter calls
      membersNS.push(memberNS);
    }
  }
  $(this).addClass(memberNS);
});
//for each member in the array, add them to the html inside the filtergroup with a class of mlMemberlist
for (var i = 0; i < members.length; i++) {
    if (members[i] != undefined) {

         $('.mlMemberlist').append('' + members[i] + '');
    }
}

/***** alphabetize those bitches *****/

$(".mlMemberlist button.abc").sort(asc_sort).appendTo(".mlMemberlist");
//$("#debug").text("Output:");
// accending sort
function asc_sort(a, b) {
  return $(b).text() < $(a).text() ? 1 : -1;
}

/*** isotope ***/

// quick search regex
var qsRegex;
var filterValue;
var $selects = $('#form-ui select');
var $checkboxes = $('#form-ui input');
var filterCheckbox;
var clearAll= $('.clear-all');
var resetFilter;
var selections;
// init Isotope

selections = function() {
    var $this = $(this).filter(".search");
  var filteredElement=$(this).clone();
  filteredElement.find(".nosearch").remove();
    var searchResult = qsRegex ? filteredElement.text().match( qsRegex ) : true;
    var selectResult = filterValue ? $(this).is(filterValue) : true;
  
    var checkboxResult = filterCheckbox ? $this.is(filterCheckbox) : true;
    var resetResult = resetFilter ? $this.is(resetFilter) : true;
    

    return searchResult && selectResult && resetResult;      

  }


var $grid = $('.grid').isotope({
  itemSelector: '.grid-item',
      percentPosition: true, // true if you use percentage widths
  filter: selections,
     getSortData: {
  username: function (itemElem) {
    return $(itemElem)
	.find('h2')
	.clone()
	.find('.field-45').remove()
	.end()
	.text()
	.toLowerCase()
	.trim(); // assuming username is in 

  },
  wing: function (itemElem) {
    return $(itemElem).attr('data-wing') || '';
  },
  section: function (itemElem) {
    return $(itemElem).attr('data-section') || '';
  },
  squad: function (itemElem) {
    return $(itemElem).attr('data-squad') || '';
  },
  drift: function (itemElem) {
    return $(itemElem).attr('data-drift') || '';
  }
}
});


 // bind sort button click
  $(".sort-by-button-group").on("click", "button", function () {
    var sortValue = $(this).attr("data-sort-value");
    $grid.isotope({ sortBy: sortValue });
  });


$("#sorts").each( function( i, buttonGroup ) {
  var $buttonGroup = $( buttonGroup );
  $buttonGroup.on( "click", "button", function() {
    $buttonGroup.find(".is-checked").removeClass("is-checked");
    $( this ).addClass("is-checked");
      
  $grid.isotope();
    
  });
});

$(".filter").each( function( i, buttonGroup ) {
  var $buttonGroup = $( buttonGroup );
  $buttonGroup.on( "click", "button", function() {
    $buttonGroup.find(".is-checked").removeClass("is-checked");
    $( this ).addClass("is-checked");
    
    filterValue = "";
    $(".filter .is-checked").each(function () {
      var data = $(this).attr("data-filter");
      if (data && data !== "*") filterValue += data.replace(" ", "");
    });

    // ➤ Show squad filters only when Rider is selected
if (filterValue.includes(".Rider")) {
  $('.grid-item .drift-info').hide();
  $('.grid-item.role-Faculty .wss-info, .grid-item.role-Frontlines .wss-info').hide();
  $('.grid-item .wss-info').not('.role-Faculty .wss-info, .role-Frontlines .wss-info').show();
  $('.grid-item .bonded').show();

  $(".drift-filters").slideUp();
  $(".squad-filters").slideDown();
  $grid.isotope({
    filter: selections,
    sortBy: ['wing', 'section', 'squad']
  });
} else if (filterValue.includes(".Flier")) {
  $('.grid-item .wss-info').hide();
  $('.grid-item.role-Faculty .drift-info, .grid-item.role-Frontlines .drift-info').hide();
  $('.grid-item .drift-info').not('.role-Faculty .drift-info, .role-Frontlines .drift-info').show();
  $('.grid-item .bonded').show();

  $(".squad-filters").slideUp();
  $(".drift-filters").slideDown();
  $grid.isotope({
    filter: selections,
    sortBy: ['drift']
  });
} else {
      $('.wss-info').hide();
      $(".squad-filters").slideUp();
      $('.drift-info').hide();
      $(".drift-filters").slideUp();
      $grid.isotope({
        filter: selections,
        sortBy: 'username'
      });
    }

  });
});

// use value of search field to filter
var $quicksearch = $(".quicksearch").keyup( debounce( function() {
  qsRegex = new RegExp( $quicksearch.val(), "gi" );
  $grid.isotope();
}) );
  
clearAll.on( 'click', function() {
    $(".squad-filters").slideUp();
    filterValue = '';
    qsRegex = '';
    $(".quicksearch").val('');

    $(".squad-filters").slideUp();
    $grid.isotope({
        filter: selections,
        sortBy: 'username'
    });

    $(".drift-filters").slideUp();
    filterValue = '';
    qsRegex = '';
    $(".quicksearch").val('');

    $(".drift-filters").slideUp();
    $grid.isotope({
        filter: selections,
        sortBy: 'username'
    });
    
    $('.drift-info').hide();
    $('.wss-info').hide();

    $(".filters").find(".is-checked").removeClass("is-checked");
    $("button.reset").addClass("is-checked");

  
});

$(".filter .see-all").on("click", function () {
  filterValue = "";

  var $group = $(this).closest(".option-set");
  $group.find(".is-checked").removeClass("is-checked");

  $(this).addClass("is-checked");

  $grid.isotope({
    filter: selections,
    sortBy: "username"
  });
});


// debounce so filtering doesn't happen every millisecond
function debounce( fn, threshold ) {
  var timeout;
  return function debounced() {
    if ( timeout ) {
      clearTimeout( timeout );
    }
    function delayed() {
      fn();
      timeout = null;
    }
    setTimeout( delayed, threshold || 100 );
  };
}
